(function() {
  $(document).ready(function() {
    $("div.holder").jPages({
      containerID: "itemContainer",
      perPage: 5,
      keyBrowse: true,
      scrollBrowse: true
    });
    $("select#Itemsperpage").change(function() {
      var newPerPage;
      newPerPage = parseInt($(this).val());
      $("div.holder").jPages("destroy").jPages({
        containerID: "itemContainer",
        perPage: newPerPage
      });
    });
    $(function() {
      $("#sales").dataTable();
    });
  });

}).call(this);
